$(document).ready(function() {
    function checkWindowSize() {
        var windowSize = $(window).width();
        if (windowSize <= 768) { // For mobile devices
            $("#filter-buttons button").css({
                "padding": "10px",
                "font-size": "14px",
                "width": "200px", // Fixed width for each button
                "box-sizing": "border-box"
            });
        } else if (windowSize > 768 && windowSize <= 1024) { // For tablets
            $("#filter-buttons button").css({
                "padding": "15px",
                "font-size": "16px"
            });
        } else { // For desktops
            $("#filter-buttons button").css({
                "padding": "20px",
                "font-size": "18px"
            });
        }
    }

    // Call the function when document is ready
    checkWindowSize();

    // Call the function every time window is resized
    $(window).resize(checkWindowSize);
});